package com.nguyenDat;import com.nguyenDat.DataDictionary.DataDictionarySQL;import com.nguyenDat.Sound.SoundApp;import javax.swing.*;import javax.swing.event.ListSelectionEvent;import javax.swing.event.ListSelectionListener;import java.awt.*;public class ListWord implements ListSelectionListener {    private static JList list  = new JList();    private JScrollPane Scroll = new JScrollPane(list) ;    public ListWord(){        list.setBounds(25,135,255,490);        list.setFont(new Font(Font.DIALOG,Font.BOLD,14));        Scroll.setBounds(25,135,255,490);        list.addListSelectionListener(this);        list.setSelectionBackground(new Color(0x1297EE));        Scroll.setBorder(BorderFactory.createLineBorder(new Color(0x07658F),3));        PanelBasic.getPaneBasic().add(Scroll);    }    public static void UpdateList(){        String w = InputApp.getTextInput().getText();        if(w.equals("")){            list.setListData(new String[]{""});        }        else {            String listWord[] = DataDictionarySQL.ListWord(w);            list.setListData(listWord);        }    }    @Override    public void valueChanged(ListSelectionEvent e) {        if(list.getSelectedIndex() != -1){            InputApp.getTextInput().setText((String) list.getSelectedValue());            SoundApp.setWord(InputApp.getTextInput().getText());            OutputApp.getOutputApp().setText(DataDictionarySQL.SearchAnhViet((String) list.getSelectedValue()));        }    }}